mapper.update:

update vd_stu
set num = #{num,jdbcType=VARCHAR},
name = #{name,jdbcType=VARCHAR},

    password = #{password,jdbcType=VARCHAR},

sex = #{sex,jdbcType=CHAR},
clsid = #{cls,jdbcType=INTEGER},
dorid = #{dor,jdbcType=INTEGER}

    ,flag = #{flag,jdbcType=INTEGER}

where id = #{id,jdbcType=INTEGER}

mapper.select:

select id, num, name, password, sex, clsid, dorid, flag
from vd_stu
where id = #{id,jdbcType=INTEGER}

SELECT s.id, d.id as did, c.id as cid, s.num, s.name, s.sex, c.name as cls, d.dornum as dor, CONCAT(b.`name`,b.`no`,'号楼') as buildName, s.flag FROM vd_stu s,vd_cls c, vd_dor d, vd_build b where s.clsid = c.id and s.dorid = d.id and d.bid = b.id
and s.num like concat('%', #{num,jdbcType=VARCHAR}, '%')
and s.name like concat('%', #{name,jdbcType=VARCHAR}, '%')
and s.password = #{password,jdbcType=VARCHAR}
and s.sex = #{sex,jdbcType=VARCHAR}
and c.name like concat('%', #{cls,jdbcType=VARCHAR}, '%')
and d.dornum like concat('%', #{dor,jdbcType=VARCHAR}, '%')
and b.id = #{buildName}
order by buildName,dor
